export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY);

// Conversations and messages utility functions
export const conversationService = {
  async getConversations() {
    const { data, error } = await supabase
      .from('conversations')
      .select(`
        *,
        patient:patients(id, name, email, phone, avatar_url)
      `)
      .order('last_activity', { ascending: false });
    
    if (error) throw error;
    return data;
  },
  
  async getConversation(id: string) {
    const { data, error } = await supabase
      .from('conversations')
      .select(`
        *,
        patient:patients(id, name, email, phone, avatar_url),
        messages(*)
      `)
      .eq('id', id)
      .order('messages.timestamp', { ascending: true })
      .single();
    
    if (error) throw error;
    return data;
  },
  
  async updateConversation(id: string, updates: any) {
    const { data, error } = await supabase
      .from('conversations')
      .update(updates)
      .eq('id', id)
      .select()
      .single();
    
    if (error) throw error;
    return data;
  },
  
  async archiveConversation(id: string) {
    return this.updateConversation(id, { is_archived: true });
  },
  
  async unarchiveConversation(id: string) {
    return this.updateConversation(id, { is_archived: false });
  },
  
  async sendMessage(conversationId: string, content: string) {
    // Update the conversation's last_activity timestamp
    await supabase
      .from('conversations')
      .update({ last_activity: new Date().toISOString() })
      .eq('id', conversationId);
    
    // Insert the new message
    const { data, error } = await supabase
      .from('messages')
      .insert({
        conversation_id: conversationId,
        content,
        is_outgoing: true,
        status: 'sent'
      })
      .select()
      .single();
    
    if (error) throw error;
    return data;
  },
  
  async addPatientFromConversation(conversation: any) {
    // Check if the conversation has patient_id
    const patientId = conversation.patient_id;
    
    if (!patientId) {
      throw new Error('No patient_id available in conversation');
    }
    
    // Get patient data from conversation
    const patientName = conversation.patient?.name || 
                       conversation.contact?.name || 
                       "Unknown Patient";
    
    const patientEmail = conversation.patient?.email || null;
    const patientPhone = conversation.patient?.phone || null;
    const patientAvatar = conversation.patient?.avatar_url || 
                          conversation.contact?.avatar || 
                          null;
    
    // Check if patient already exists
    const { data: existingPatient, error: checkError } = await supabase
      .from('patients')
      .select('id')
      .eq('id', patientId)
      .maybeSingle();
    
    if (checkError) {
      console.error('Error checking existing patient:', checkError);
      throw checkError;
    }
    
    // If patient exists, update their data
    if (existingPatient) {
      const { data: updatedPatient, error: updateError } = await supabase
        .from('patients')
        .update({
          name: patientName,
          email: patientEmail,
          phone: patientPhone,
          avatar_url: patientAvatar,
          updated_at: new Date().toISOString()
        })
        .eq('id', patientId)
        .select()
        .single();
      
      if (updateError) {
        console.error('Error updating patient:', updateError);
        throw updateError;
      }
      
      return updatedPatient;
    }
    
    // Create a new patient if they don't exist
    const { data: newPatient, error: insertError } = await supabase
      .from('patients')
      .insert({
        id: patientId,
        name: patientName,
        email: patientEmail,
        phone: patientPhone,
        avatar_url: patientAvatar
      })
      .select()
      .single();
    
    if (insertError) {
      console.error('Error creating patient:', insertError);
      throw insertError;
    }
    
    return newPatient;
  }
};
